    def test_apply_intensity_transform(self):
        image = np.array([[1, 0, 2],
                          [0, 0, 0],
                          [1, 2, 2],
                          [0, 4, 0]], dtype="uint8")
        
        transform_0 = np.array([2, 4, 0, 1, 3], dtype="uint8")
        transform_1 = np.array([0, 1, 1, 2, 3], dtype="uint8")
        
        output_0 = A01.apply_intensity_transform(image=image, int_transform=transform_0)
        output_1 = A01.apply_intensity_transform(image=image, int_transform=transform_1)
        
        self.assertEqual(output_0.shape, image.shape, "Wrong shape")
        self.assertEqual(output_0.dtype, np.uint8, "Wrong type")
        
        self.assertEqual(output_1.shape, image.shape, "Wrong shape")
        self.assertEqual(output_1.dtype, np.uint8, "Wrong type")
                        
        ground_0 = np.array([[4, 2, 0], 
                             [2, 2, 2], 
                             [4, 0, 0], 
                             [2, 3, 2]], dtype="uint8")
        
        ground_1 = np.array([[1, 0, 1], 
                             [0, 0, 0], 
                             [1, 1, 1], 
                             [0, 3, 0]], dtype="uint8")        
                                            
        GT.check_for_unequal("Failed on apply_intensity_transform", "first transform", output_0, ground_0)   
        GT.check_for_unequal("Failed on apply_intensity_transform", "second transform", output_1, ground_1)   
        
        
